import React, { useState } from 'react';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';
import './ayuda.css'; // Archivo CSS para los estilos de la página

const Ayuda = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [submitted, setSubmitted] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aquí puedes agregar la lógica para enviar el mensaje al servidor
        console.log('Nombre:', name);
        console.log('Correo:', email);
        console.log('Mensaje:', message);
        setSubmitted(true);
    };

    return (
        <div>
            <Header />
            <main className="ayuda-container">
                <h2>Ayuda en Línea</h2>
                {!submitted ? (
                    <form className="ayuda-form" onSubmit={handleSubmit}>
                         <label>Nombre</label>
                        <div className="form-group">
                            <label>Nombre</label>
                            <input
                                type="text"
                                placeholder="Ingresa tu nombre"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Correo Electrónico</label>
                            <input
                                type="email"
                                placeholder="Ingresa tu correo electrónico"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Mensaje</label>
                            <textarea
                                placeholder="Escribe tu mensaje"
                                value={message}
                                onChange={(e) => setMessage(e.target.value)}
                                required
                            ></textarea>
                        </div>
                        <button type="submit" className="btn-primary">Enviar Mensaje</button>
                    </form>
                ) : (
                    <div className="confirmation-message">
                        <h3>¡Gracias por contactarnos!</h3>
                        <p>Tu mensaje ha sido enviado. Nos pondremos en contacto contigo pronto.</p>
                    </div>
                )}
            </main>
            <Footer />
        </div>
    );
};

export default Ayuda;
