import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import Header from '../components/header/Header'; // Importa el Header
import Footer from '../components/footer/Footer'; // Importa el Footer
import ProductCard from '../components/product-card/ProductCard';
import './menu-pack.css';

const MenuPack = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filter, setFilter] = useState('RICE-MENU');

    useEffect(() => {
        const fetchMenuItems = async () => {
            try {
                const response = await fetch('https://federico-fazbear.onrender.com/api/menugeneral/all');
                if (!response.ok) {
                    throw new Error('Error al cargar el menú');
                }
                const data = await response.json();
                setProducts(data.menuGenerals);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchMenuItems();
    }, []);

    const addToCart = (item) => {
        // Aquí podrías llamar a una función de tu carrito para agregar el producto
        console.log("Añadido al carrito:", item);
    };

    if (loading) return <div>Cargando...</div>;
    if (error) return <div>{error}</div>;

    return (
        <>
            <Header />
            <section>
                <Container>
                    <Row>
                        <Col lg="12" className="text-center mb-4">
                            <h3 className="menu__title">Nuestras Cartas</h3>
                        </Col>
                        <Col lg="12" className="text-center mb-5">
                            <button className={`filter-btn ${filter === 'DESAYUNOS' ? 'active__btn' : ''}`} onClick={() => setFilter('FAST-FOOD')}>Fast Food</button>
                            <button className={`filter-btn ${filter === 'ALMUERZOS' ? 'active__btn' : ''}`} onClick={() => setFilter('RICE-MENU')}>Rice Food</button>
                            <button className={`filter-btn ${filter === 'INFANTIL' ? 'active__btn' : ''}`} onClick={() => setFilter('PIZZA')}>Pizza</button>
                            <button className={`filter-btn ${filter === 'POSTRES' ? 'active__btn' : ''}`} onClick={() => setFilter('DESSERT')}>Dessert</button>
                            <button className={`filter-btn ${filter === 'BEBIDAS' ? 'active__btn' : ''}`} onClick={() => setFilter('COFFEE')}>Coffee</button>
                        </Col>
                        {
                            products.filter(item => item.idTipoMenu === filter).map(item => (
                                <Col lg="3" md="4" sm="6" xs="6" key={item.idAlimento} className="mb-4">
                                    <ProductCard item={item} addToCart={addToCart} />
                                </Col>
                            ))
                        }
                    </Row>
                </Container>
            </section>
            <Footer />
        </>
    );
}

export default MenuPack;
