import React, { useState, useContext } from 'react';
import { CartContext } from '../cart/CartContext';
import './product-card.css';

const ProductCard = (props) => {
    const { nombre, imagen, precio, descripcion } = props.item;
    const { addToCart } = useContext(CartContext); // Usa el contexto del carrito
    const [quantity, setQuantity] = useState(1); // Estado para la cantidad

    const handleAddToCart = () => {
        // Llama a la función addToCart con el item y la cantidad
        addToCart({ ...props.item, quantity });
        setQuantity(1); // Resetea la cantidad después de agregar
    };

    return (
        <div className="single__product">
            <div className="product__img">
                <img src={imagen} alt={nombre} className="w-100" />
            </div>
            <div className="product__content">
                <div className="rating text-center">
                    <span><i className="ri-star-s-line"></i></span>
                    <span><i className="ri-star-s-line"></i></span>
                    <span><i className="ri-star-s-line"></i></span>
                    <span><i className="ri-star-s-line"></i></span>
                    <span><i className="ri-star-s-line"></i></span>
                </div>
                <h6>{nombre}</h6>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">Precio: Q<span>{precio}</span></span>
                    <span className="shopping__icon" onClick={handleAddToCart}>
                        <i className="ri-shopping-cart-2-line"></i>
                    </span>
                </div>
                <p>{descripcion}</p>
                {/* Campo para seleccionar cantidad */}
                <input
                    type="number"
                    min="1"
                    value={quantity}
                    onChange={(e) => setQuantity(Math.max(1, e.target.value))}
                />
                <button onClick={handleAddToCart}>Agregar al Carrito</button>
            </div>
        </div>
    );
};
export default ProductCard;
