import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../cart/CartContext';
import './cart.css';

const Cart = () => {
    const { cartItems, removeFromCart } = useContext(CartContext);
    const [quantitiesToRemove, setQuantitiesToRemove] = useState({});

    if (cartItems.length === 0) {
        return <div className="empty-cart">El carrito está vacío</div>;
    }

    const handleRemoveQuantity = (itemId) => {
        const quantity = quantitiesToRemove[itemId] || 0;
        if (quantity > 0) {
            // Lógica para eliminar la cantidad especificada
            removeFromCart(itemId, quantity);
            // Restablecer la cantidad ingresada
            setQuantitiesToRemove((prev) => ({ ...prev, [itemId]: 0 }));
        }
    };

    return (
        <div className="cart-container">
            <Link to="/menu-pack" className="back-to-menu">← Volver al Menú</Link> {/* Botón para volver al menú */}
            <h2>Carrito</h2>
            <ul className="cart-items">
                {cartItems.map((item) => (
                    <li key={item.idAlimento} className="cart-item">
                        <img src={item.imagen} alt={item.nombre} className="cart-item-image" />
                        <div className="cart-item-details">
                            <h5>{item.nombre}</h5>
                            <p>Precio: Q{item.precio}</p>
                            <p>Cantidad: {item.quantity}</p>
                            <p>Total: Q{(item.precio * item.quantity).toFixed(2)}</p>
                        </div>
                        <div className="remove-quantity">
                            <input
                                type="number"
                                value={quantitiesToRemove[item.idAlimento] || 0}
                                onChange={(e) => setQuantitiesToRemove({ ...quantitiesToRemove, [item.idAlimento]: Number(e.target.value) })}
                                min="1"
                                max={item.quantity}
                            />
                            <button onClick={() => handleRemoveQuantity(item.idAlimento)}>Eliminar</button>
                        </div>
                    </li>
                ))}
            </ul>
            <div className="cart-total">
                <h4>Total: Q{cartItems.reduce((acc, item) => acc + item.precio * item.quantity, 0).toFixed(2)}</h4>
            </div>
        </div>
    );
};
export default Cart;
