import React, { createContext, useState, useContext, useEffect } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState(() => {
        // Cargar los artículos del carrito desde el almacenamiento local al iniciar
        const savedCartItems = localStorage.getItem('cartItems');
        return savedCartItems ? JSON.parse(savedCartItems) : [];
    });

    useEffect(() => {
        // Guarda los artículos del carrito en el almacenamiento local cada vez que cambien
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }, [cartItems]);

    const addToCart = (item) => {
        setCartItems((prevItems) => {
            const existingItemIndex = prevItems.findIndex(cartItem => cartItem.id === item.id);

            if (existingItemIndex !== -1) {
                // Si el artículo ya existe, actualiza la cantidad
                const updatedItems = [...prevItems];
                updatedItems[existingItemIndex].quantity += item.quantity; // Suma la nueva cantidad
                return updatedItems;
            } else {
                // Si no existe, agrega el nuevo artículo con su cantidad
                return [...prevItems, { ...item }];
            }
        });
        console.log("Item added to cart:", item); // Para depuración
    };

    const updateCartItemQuantity = (itemId, newQuantity) => {
        setCartItems((prevItems) => {
            return prevItems
                .map(item =>
                    item.id === itemId
                        ? { ...item, quantity: newQuantity }
                        : item
                )
                .filter(item => item.quantity > 0); // Filtra los artículos con cantidad mayor a cero
        });
        console.log("Updated item quantity:", itemId, newQuantity); // Para depuración
    };

    return (
        <CartContext.Provider value={{ cartItems, addToCart, updateCartItemQuantity }}>
            {children}
        </CartContext.Provider>
    );
};

// Hook para usar el contexto de carrito
export const useCart = () => {
    return useContext(CartContext);
};
