import React, { useContext, useState } from 'react';
import { CartContext } from '../cart/CartContext';

const ProductCard = (props) => {
    const { nombre, imagen, precio, descripcion, idAlimento } = props.item;
    const { addToCart } = useContext(CartContext);
    const [quantity, setQuantity] = useState(1);

    const handleAddToCart = (event) => {
        event.preventDefault(); // Previene la recarga de la página
        if (quantity < 1) return; // Asegúrate de que la cantidad sea al menos 1
        const itemToAdd = {
            ...props.item,
            quantity: quantity, // Establece la cantidad seleccionada
        };
        addToCart(itemToAdd); // Agrega el producto al carrito
    };

    return (
        <div className="single__product">
            <div className="product__img">
                <img src={imagen} alt={nombre} className="w-100" />
            </div>
            <div className="product__content">
                <h6>{nombre}</h6>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price">Precio: Q{precio}</span>
                </div>
                <p>{descripcion}</p>
                <div className="d-flex align-items-center justify-content-between">
                    <input
                        type="number"
                        min="1"
                        value={quantity}
                        onChange={(e) => setQuantity(parseInt(e.target.value))}
                        className="quantity-input"
                    />
                    <button onClick={handleAddToCart} className="add-to-cart-btn">
                        Agregar al carrito
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ProductCard;
