import React, { createContext, useState, useContext, useEffect } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState(() => {
        // Recuperar los elementos del carrito desde localStorage al iniciar
        const storedCart = localStorage.getItem('cartItems');
        return storedCart ? JSON.parse(storedCart) : [];
    });

    // Funci贸n para actualizar el carrito en el estado y en localStorage
    const updateCart = (items) => {
        setCartItems(items);
        localStorage.setItem('cartItems', JSON.stringify(items));
    };

    const addToCart = (item) => {
        const updatedCart = [...cartItems, item];
        updateCart(updatedCart);
        console.log("Item added to cart:", item); // Para depuraci贸n
    };

    const removeFromCart = (itemId, quantityToRemove) => {
        setCartItems((prevItems) => {
            const item = prevItems.find(item => item.id === itemId);
            if (item) {
                const newQuantity = item.quantity - quantityToRemove;
                if (newQuantity <= 0) {
                    return prevItems.filter(item => item.id !== itemId); // Eliminar completamente si la cantidad es 0 o menos
                }
                return prevItems.map(item => 
                    item.id === itemId ? { ...item, quantity: newQuantity } : item
                ); // Actualizar la cantidad del item
            }
            return prevItems;
        });
    };
    

    // Efecto para sincronizar el estado del carrito con localStorage cuando cambia
    useEffect(() => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }, [cartItems]);

    return (
        <CartContext.Provider value={{ cartItems, addToCart, removeFromCart }}>
            {children}
        </CartContext.Provider>
    );
};

// Hook para usar el contexto de carrito
export const useCart = () => {
    return useContext(CartContext);
};
