import React, { createContext, useState, useContext } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState([]);

    const addToCart = (item) => {
        setCartItems((prevItems) => {
            const existingItem = prevItems.find((cartItem) => cartItem.id === item.id);
            
            if (existingItem) {
                // Si el alimento ya existe, se suma la cantidad
                return prevItems.map((cartItem) =>
                    cartItem.id === item.id
                        ? { ...cartItem, quantity: cartItem.quantity + item.quantity }
                        : cartItem
                );
            } else {
                // Si no existe, se agrega el nuevo item con la cantidad inicial
                return [...prevItems, { ...item, quantity: item.quantity }];
            }
        });
        console.log("Item added to cart:", item); // Para depuración
    };

    const updateCartItemQuantity = (itemId, newQuantity) => {
        setCartItems((prevItems) => {
            return prevItems
                .map(item =>
                    item.id === itemId
                        ? { ...item, quantity: newQuantity }
                        : item
                )
                .filter(item => item.quantity > 0); // Filtra los artículos con cantidad mayor a cero
        });
        console.log("Updated item quantity:", itemId, newQuantity); // Para depuración
    };

    return (
        <CartContext.Provider value={{ cartItems, addToCart, updateCartItemQuantity }}>
            {children}
        </CartContext.Provider>
    );
};

// Hook para usar el contexto de carrito
export const useCart = () => {
    return useContext(CartContext);
};
