import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom'; // Importa Link
import { CartContext } from '../cart/CartContext';
import './cart.css';

const Cart = () => {
    const { cartItems, updateCartItemQuantity } = useContext(CartContext);
    const [selectedItemIndex, setSelectedItemIndex] = useState(null);
    const [selectedQuantity, setSelectedQuantity] = useState(1);

    if (cartItems.length === 0) {
        return <div className="empty-cart">El carrito est√° vac√≠o</div>;
    }

    const handleDeleteClick = (index) => {
        setSelectedItemIndex(index);
        setSelectedQuantity(1); // Resetea la cantidad seleccionada al hacer clic en eliminar
    };

    const handleQuantityChange = (event) => {
        setSelectedQuantity(Number(event.target.value));
    };

    const handleConfirmDelete = () => {
        if (selectedItemIndex !== null) {
            const itemToUpdate = cartItems[selectedItemIndex];
            const newQuantity = itemToUpdate.quantity - selectedQuantity;
            updateCartItemQuantity(itemToUpdate.id, newQuantity > 0 ? newQuantity : 0);
            setSelectedItemIndex(null);
        }
    };

    return (
        <div className="cart-container">
            <Link to="/menu-pack" className="back-to-menu">‚Üê Volver al Men√∫</Link>
            <h2>Carrito</h2>
            <ul className="cart-items">
                {cartItems.map((item, index) => (
                    <li key={index} className="cart-item">
                        <img src={item.imagen} alt={item.nombre} className="cart-item-image" />
                        <div className="cart-item-details">
                            <h5>{item.nombre}</h5>
                            <p>Precio: Q{item.precio}</p>
                            <p>Cantidad: {item.quantity}</p>
                            <p>Total: Q{(item.precio * item.quantity).toFixed(2)}</p>
                            <button className="delete-button" onClick={() => handleDeleteClick(index)}>
                                üóëÔ∏è Eliminar
                            </button>
                            {selectedItemIndex === index && (
                                <div className="delete-options">
                                    <label>Selecciona la cantidad a eliminar:</label>
                                    <input
                                        type="number"
                                        min="1"
                                        max={item.quantity}
                                        value={selectedQuantity}
                                        onChange={handleQuantityChange}
                                    />
                                    <button onClick={handleConfirmDelete}>Confirmar</button>
                                    <button onClick={() => setSelectedItemIndex(null)}>Cancelar</button>
                                </div>
                            )}
                        </div>
                    </li>
                ))}
            </ul>
            <div className="cart-total">
                <h4>Total: Q{cartItems.reduce((acc, item) => acc + item.precio * item.quantity, 0).toFixed(2)}</h4>
            </div>
        </div>
    );
};


export default Cart;
