import React, { createContext, useState, useContext, useEffect } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState(() => {
        const storedCart = localStorage.getItem('cartItems');
        return storedCart ? JSON.parse(storedCart) : [];
    });

    const updateCart = (items) => {
        setCartItems(items);
        localStorage.setItem('cartItems', JSON.stringify(items));
    };

    const addToCart = (item) => {
        const existingItemIndex = cartItems.findIndex(cartItem => cartItem.idAlimento === item.idAlimento);
        if (existingItemIndex >= 0) {
            // Si el producto ya está en el carrito, actualiza la cantidad
            const updatedCartItems = [...cartItems];
            updatedCartItems[existingItemIndex].quantity += item.quantity;
            updateCart(updatedCartItems);
        } else {
            // Si el producto no está en el carrito, agrégalo como un nuevo ítem
            const newItem = { ...item, quantity: item.quantity };
            updateCart([...cartItems, newItem]);
        }
        console.log("Item added to cart:", item);
    };
    const removeFromCart = (itemId, quantityToRemove) => {
        const updatedCart = cartItems.map((item) => {
            if (item.idAlimento === itemId) {
                item.quantity -= quantityToRemove;
                return item;
            }
            return item;
        }).filter(item => item.quantity > 0); // Filtra los productos con cantidad mayor a cero
        updateCart(updatedCart);
        console.log("Item removed from cart:", itemId);
    };

    useEffect(() => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }, [cartItems]);

    return (
        <CartContext.Provider value={{ cartItems, addToCart, removeFromCart }}>
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => {
    return useContext(CartContext);
};
