import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../cart/CartContext';
import './cart.css';

const Cart = () => {
    const { cartItems, removeFromCart } = useContext(CartContext);
    const [quantitiesToRemove, setQuantitiesToRemove] = useState({}); // Estado para cantidades a eliminar

    if (cartItems.length === 0) {
        return <div className="empty-cart">El carrito está vacío</div>;
    }

    const handleRemove = (item) => {
        const quantityToRemove = parseInt(quantitiesToRemove[item.idAlimento]) || 0; // Obtener la cantidad a eliminar
        if (quantityToRemove > 0) {
            removeFromCart(item.idAlimento, quantityToRemove); // Llamar a la función de eliminación
            setQuantitiesToRemove({ ...quantitiesToRemove, [item.idAlimento]: '' }); // Resetear el input
        }
    };

    return (
        <div className="cart-container">
            <Link to="/menu-pack" className="back-to-menu">← Volver al Menú</Link>
            <h2>Carrito</h2>
            <ul className="cart-items">
                {cartItems.map((item, index) => (
                    <li key={index} className="cart-item">
                        <img src={item.imagen} alt={item.nombre} className="cart-item-image" />
                        <div className="cart-item-details">
                            <h5>{item.nombre}</h5>
                            <p>Precio: Q{item.precio}</p>
                            <p>Cantidad: {item.quantity}</p>
                            <p>Total: Q{(item.precio * item.quantity).toFixed(2)}</p>
                            <input 
                                type="number" 
                                min="1" 
                                value={quantitiesToRemove[item.idAlimento] || ''} 
                                onChange={(e) => 
                                    setQuantitiesToRemove({
                                        ...quantitiesToRemove, 
                                        [item.idAlimento]: e.target.value
                                    })
                                } 
                                placeholder="Cantidad a eliminar" 
                            />
                            <button onClick={() => handleRemove(item)}>Eliminar</button>
                        </div>
                    </li>
                ))}
            </ul>
            <div className="cart-total">
                <h4>Total: Q{cartItems.reduce((acc, item) => acc + item.precio * item.quantity, 0).toFixed(2)}</h4>
            </div>
        </div>
    );
};

export default Cart;
