import React, { useState } from 'react';
import CartIcon from './CartIcon';
import CartDropdown from './CartDropdown';
import './Cart.css'; // Estilos adicionales si son necesarios

const Cart = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [cartItems, setCartItems] = useState([
        { name: 'Producto 1', price: 10, quantity: 2 },
        { name: 'Producto 2', price: 15, quantity: 1 },
    ]);

    const toggleCart = () => {
        setIsOpen(!isOpen);
    };

    const removeItem = (index) => {
        const newCartItems = [...cartItems];
        newCartItems.splice(index, 1);
        setCartItems(newCartItems);
    };

    return (
        <div className="cart-container">
            <CartIcon itemCount={cartItems.length} onClick={toggleCart} />
            {isOpen && <CartDropdown items={cartItems} onRemoveItem={removeItem} />}
        </div>
    );
};

export default Cart;
