import React, {useRef} from 'react'
import './header.css' 
import { Container } from 'reactstrap'
import logo from '../../assets/images/Logo.png';

const navLinks =  [

    {
        label: 'Home',
        url: '#'
    },
    {
        label: 'About',
        url: '#'
    },
    {
        label: 'Reservas',
        url: '#'
    },
    {
        label: 'Recipe',
        url: '#'
    },
    {
        label: 'Contact',
        url: '#'
    },

]
const Header = () => {
 const menuRef = useRef()
 const menuToggle = () => menuRef.current.classList.toggle('active__menu')

    return ( 
    <header className="header">
     <Container>
      <div className="navigation">
       <div className="logo" style={{ display: 'flex', alignItems: 'center' }}>
        <img 
            src={logo}
            alt="Federico Fazbear Logo"
            style={{ width: '100px', height: 'auto', marginRight: '10px' }}/>
             <h2>
            <span></span> Federico Fazbear
            </h2>
        </div>
        <div className="nav__menu " ref={menuRef}>
         <div className="nav__list__wrapper d-flex align-items-center gap-5">
            <ul className="nav__list">
             {navLinks.map((item,index) => (
                <li className="nav__item" key={index}>
                    <a href={item.url} onClick={menuToggle}
                    >{item.label}</a>  
                </li>
            ))}
        </ul>   
        <div className="menu_right">
        <div className="custom__search">
        <input type="text" placeholder="Search..."/>
        <span><i class="ri-search-line"></i></span>
        </div>
                </div>
            </div>
                </div>

    
            <div className="mobile_menu">
                <span><i class="ri-menu-line" onClick={menuToggle}></i> </span>
            </div>
        </div>
        </Container>
    
        </header>
    
        )
};

export default Header;
