import React, { createContext, useState, useContext, useEffect } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState(() => {
        // Intenta obtener los elementos del carrito del localStorage al cargar la página
        try {
            const savedCart = localStorage.getItem('cartItems');
            return savedCart ? JSON.parse(savedCart) : [];
        } catch (error) {
            console.error("Error al cargar el carrito desde localStorage:", error);
            return [];
        }
    });

    // Guarda el carrito en el localStorage cada vez que cambia el estado
    useEffect(() => {
        try {
            localStorage.setItem('cartItems', JSON.stringify(cartItems));
        } catch (error) {
            console.error("Error al guardar el carrito en localStorage:", error);
        }
    }, [cartItems]);

    const addToCart = (item, quantity = 1) => {
        setCartItems((prevItems) => {
            const existingItemIndex = prevItems.findIndex(cartItem => cartItem.id === item.id);
    
            if (existingItemIndex !== -1) {
                // Si el artículo ya está en el carrito, actualiza la cantidad
                const updatedItems = [...prevItems];
                updatedItems[existingItemIndex].quantity += quantity; // Sumar la nueva cantidad
                return updatedItems;
            } else {
                // Si no existe, agrega el artículo al carrito
                return [...prevItems, { ...item, quantity }];
            }
        });
        console.log("Item added to cart:", item, "Cantidad:", quantity); // Para depuración
    };
    

    const updateCartItemQuantity = (itemId, newQuantity) => {
        try {
            setCartItems((prevItems) => {
                return prevItems
                    .map(item =>
                        item.id === itemId
                            ? { ...item, quantity: newQuantity }
                            : item
                    )
                    .filter(item => item.quantity > 0); // Filtra los artículos con cantidad mayor a cero
            });
            console.log("Updated item quantity:", itemId, newQuantity); // Para depuración
        } catch (error) {
            console.error("Error al actualizar la cantidad del artículo:", error);
        }
    };

    return (
        <CartContext.Provider value={{ cartItems, addToCart, updateCartItemQuantity }}>
            {children}
        </CartContext.Provider>
    );
};

// Hook para usar el contexto de carrito
export const useCart = () => {
    return useContext(CartContext);
};
