import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import Header from '../components/header/Header'; // Importa el Header
import Footer from '../components/footer/Footer'; // Importa el Footer
import ProductCard from '../components/product-card/ProductCard';
import './menu-pack.css';

const MenuPack = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [cart, setCart] = useState([]);

    useEffect(() => {
        const fetchMenuItems = async () => {
            try {
                const response = await fetch('https://federico-fazbear.onrender.com/api/menugeneral/all');
                if (!response.ok) {
                    throw new Error('Error al cargar el menú');
                }
                const data = await response.json();
                setProducts(data.menuGenerals);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchMenuItems();
    }, []);

    const addToCart = (item) => {
        setCart(prevCart => {
            const existingItem = prevCart.find(cartItem => cartItem.nombre === item.nombre);
            if (existingItem) {
                return prevCart.map(cartItem =>
                    cartItem.nombre === item.nombre
                        ? { ...existingItem, quantity: existingItem.quantity + 1 }
                        : cartItem
                );
            } else {
                return [...prevCart, { nombre: item.nombre, precio: item.precio, quantity: 1 }];
            }
        });
    };

    if (loading) return <div>Cargando...</div>;
    if (error) return <div>{error}</div>;

    return (
        <>
            <Header />
            <Cart addToCart={addToCart} /> {/* Pasa la función addToCart */}
            <section>
                <Container>
                    <Row>
                        <Col lg="12" className="text-center mb-4">
                            <h3 className="menu__title">Nuestras Cartas</h3>
                        </Col>
                        <Col lg="12" className="text-center mb-5">
                            {/* Filtros aquí */}
                        </Col>
                        {products.map(item => (
                            <Col lg="3" md="4" sm="6" xs="6" key={item.idAlimento} className="mb-4">
                                <ProductCard item={item} addToCart={addToCart} />
                            </Col>
                        ))}
                    </Row>
                </Container>
            </section>
            <Footer />
        </>
    );
};
export default MenuPack;
