import React, { createContext, useState, useContext, useEffect } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState(() => {
        // Recuperar los elementos del carrito desde localStorage al iniciar
        const storedCart = localStorage.getItem('cartItems');
        return storedCart ? JSON.parse(storedCart) : [];
    });

    // Función para actualizar el carrito en el estado y en localStorage
    const updateCart = (items) => {
        setCartItems(items);
        localStorage.setItem('cartItems', JSON.stringify(items));
    };

    const addToCart = (item) => {
        const updatedCart = [...cartItems, item];
        updateCart(updatedCart);
        console.log("Item added to cart:", item); // Para depuración
    };

    const removeFromCart = (itemId) => {
        const updatedCart = cartItems.filter((item) => item.id !== itemId);
        updateCart(updatedCart);
        console.log("Item removed from cart:", itemId); // Para depuración
    };

    // Efecto para sincronizar el estado del carrito con localStorage cuando cambia
    useEffect(() => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }, [cartItems]);

    return (
        <CartContext.Provider value={{ cartItems, addToCart, removeFromCart }}>
            {children}
        </CartContext.Provider>
    );
};

// Hook para usar el contexto de carrito
export const useCart = () => {
    return useContext(CartContext);
};
