import React, { useState } from 'react';
import CartIcon from './CartIcon';
import CartDropdown from './CartDropdown';
import './Cart.css'; // Estilos adicionales si son necesarios

const Cart = ({ addToCart }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [cartItems, setCartItems] = useState([]);

    const toggleCart = () => {
        setIsOpen(!isOpen);
    };

    const addItem = (item) => {
        const existingItem = cartItems.find(cartItem => cartItem.name === item.nombre);
        if (existingItem) {
            // Si el producto ya est치 en el carrito, solo aumentamos la cantidad
            setCartItems(cartItems.map(cartItem => 
                cartItem.name === item.nombre
                    ? { ...existingItem, quantity: existingItem.quantity + 1 }
                    : cartItem
            ));
        } else {
            // Si el producto no est치, lo agregamos
            setCartItems([...cartItems, { name: item.nombre, price: item.precio, quantity: 1 }]);
        }
    };

    const removeItem = (index) => {
        const newCartItems = [...cartItems];
        newCartItems.splice(index, 1);
        setCartItems(newCartItems);
    };

    return (
        <div className="cart-container">
            <CartIcon itemCount={cartItems.length} onClick={toggleCart} />
            {isOpen && <CartDropdown items={cartItems} onRemoveItem={removeItem} />}
        </div>
    );
};

export default Cart;
