import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../cart/CartContext';
import './cart.css';

const Cart = () => {
    const { cartItems, removeFromCart } = useContext(CartContext);
    const [quantitiesToRemove, setQuantitiesToRemove] = useState({}); // Estado para manejar las cantidades a eliminar

    if (cartItems.length === 0) {
        return <div className="empty-cart">El carrito está vacío</div>;
    }

    const handleRemove = (itemId) => {
        const quantity = quantitiesToRemove[itemId] || 0; // Obtiene la cantidad especificada o 0
        removeFromCart(itemId, quantity); // Llama a la función para eliminar del carrito
        setQuantitiesToRemove((prev) => ({ ...prev, [itemId]: 0 })); // Restablece la cantidad después de eliminar
    };

    return (
        <div className="cart-container">
            <Link to="/menu-pack" className="back-to-menu">← Volver al Menú</Link> {/* Botón para volver al menú */}
            <h2>Carrito</h2>
            <ul className="cart-items">
                {cartItems.map((item, index) => (
                    <li key={index} className="cart-item">
                        <img src={item.imagen} alt={item.nombre} className="cart-item-image" />
                        <div className="cart-item-details">
                            <h5>{item.nombre}</h5>
                            <p>Precio: Q{item.precio}</p>
                            <p>Cantidad: {item.quantity}</p>
                            <p>Total: Q{(item.precio * item.quantity).toFixed(2)}</p>
                            {/* Campo de entrada para la cantidad a eliminar */}
                            <input
                                type="number"
                                min="1"
                                value={quantitiesToRemove[item.id] || ''}
                                onChange={(e) => setQuantitiesToRemove((prev) => ({
                                    ...prev,
                                    [item.id]: Number(e.target.value)
                                }))}
                                placeholder="Cantidad a eliminar"
                            />
                            <button onClick={() => handleRemove(item.id)}>Eliminar</button> {/* Botón de eliminar */}
                        </div>
                    </li>
                ))}
            </ul>
            <div className="cart-total">
                <h4>Total: Q{cartItems.reduce((acc, item) => acc + item.precio * item.quantity, 0).toFixed(2)}</h4>
            </div>
        </div>
    );
};

export default Cart;
