import React, { useContext, useState } from 'react';
import { CartContext } from '../cart/CartContext';
import './product-card.css';

const ProductCard = (props) => {
    const { nombre, imagen, precio, descripcion } = props.item;
    const { addToCart } = useContext(CartContext);
    const [quantity, setQuantity] = useState(1); // Estado para la cantidad del producto

    const handleAddToCart = (event) => {
        event.stopPropagation(); // Evita la propagación del evento
        event.preventDefault(); // Previene la recarga de la página

        if (quantity < 1) return; // Evita agregar cantidades no válidas

        // Crear un objeto con la información del producto y la cantidad seleccionada
        const itemToAdd = {
            ...props.item,
            quantity: quantity,
        };
        addToCart(itemToAdd); // Agrega el producto al carrito
    };

    return (
        <div className="single__product">
            <div className="product__img">
                <img src={imagen} alt={nombre} className="w-100" />
            </div>
            <div className="product__content">
                <div className="rating text-center">
                    <span><i className="ri-star-s-line"></i></span>
                    <span><i className="ri-star-s-line"></i></span>
                    <span><i className="ri-star-s-line"></i></span>
                    <span><i className="ri-star-s-line"></i></span>
                    <span><i className="ri-star-s-line"></i></span>
                </div>
                <h6>{nombre}</h6>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">Precio: Q<span>{precio}</span></span>
                    <span className="shopping__icon">
                        <i className="ri-shopping-cart-2-line" onClick={handleAddToCart}></i>
                    </span>
                </div>
                <p>{descripcion}</p>
                <div className="quantity-selector">
                    <label>Cantidad:</label>
                    <input
                        type="number"
                        min="0"
                        value={quantity}
                        onChange={(e) => setQuantity(parseInt(e.target.value))}
                    />
                </div>
            </div>
        </div>
    );
};

export default ProductCard;
