import React, { useContext } from 'react';
import { CartContext } from '../cart/CartContext';
import './cart.css';

const Cart = () => {
    const { cartItems, removeFromCart, clearCart } = useContext(CartContext);
    
    const totalPrice = cartItems.reduce((total, item) => total + item.precio, 0);

    return (
        <div className="cart">
            <h3>Carrito de Compras</h3>
            {cartItems.length === 0 ? (
                <p>No hay productos en el carrito.</p>
            ) : (
                <>
                    <ul>
                        {cartItems.map(item => (
                            <li key={item.idAlimento}>
                                <img src={item.imagen} alt={item.nombre} />
                                <span>{item.nombre}</span>
                                <span>Q{item.precio}</span>
                                <button onClick={() => removeFromCart(item.idAlimento)}>Eliminar</button>
                            </li>
                        ))}
                    </ul>
                    <div>
                        <p>Total: Q{totalPrice.toFixed(2)}</p>
                        <button onClick={clearCart}>Vaciar Carrito</button>
                        <button>Finalizar Compra</button>
                    </div>
                </>
            )}
        </div>
    );
};

export default Cart;
